		DEVICE ZXSPECTRUM128	       ORG     #8000;	       ENT	       DI	       LD      HL,int	       LD      (#60ff),HL	       LD      A,#60	       LD      I,A	       IM      2	       LD      HL,#c000	       LD      DE,#4000	       LD      BC,6912	       LDIR	       LD      A,#17	       LD      BC,#7ffd	       OUT     (C),A	       LD      HL,#4000	       LD      DE,#c000	       LD      BC,6912	       LDIR	       LD      A,#10	       LD      BC,#7ffd	       OUT     (C),A	       EI	       LD      A,#40	       LD      (ytabl),A	       LD      DE,#4000	       LD      HL,ytabl+2	       LD      B,95kaka	       CALL    downde	       LD      A,E	       LD      (HL),A	       INC     HL	       LD      A,D	       LD      (HL),A	       INC     HL	       DJNZ    kakassaka	       CALL    rnd	       EX      DE,HL	       LD      HL,(sklad)	       EX      DE,HL	       CALL    prstr	       INC     DE	       LD      A,(DE)	       CP      1	       CALL    Z,konetz	       EX      DE,HL	       LD      (sklad),HL	       CALL    cls	       CALL    cls	       CALL    cls	       LD      A,127	       IN      A,(#fe)	       BIT     0,A	       JR      NZ,ssaka	       DI	       LD      A,63	       LD      I,A	       IM      1	       LD      A,#10	       LD      BC,#7ffd	       OUT     (C),A	       EI	       RETint	       DI	       PUSH    AF,BC,DE,HL,IX,IYhhh	       LD      A,#10	       LD      BC,#7ffd	       OUT     (C),A	       LD      A,(hhh+1)	       XOR     8	       LD      (hhh+1),A	       POP     IY,IX,HL,DE,BC,AF	       EI	       RETkonetz	       LD      DE,txt	       RETcls	       LD      A,(faza)	       CP      16	       CALL    Z,dermo	       LD      H,0	       LD      L,A	       ADD     HL,HL	       ADD     HL,HL	       ADD     HL,HL	       LD      DE,chclr	       ADD     HL,DE	       EX      DE,HL	       HALT	       LD      HL,#4000	       LD      IX,#c000	       CALL    clr	       LD      HL,#4800	       LD      IX,#c800	       CALL    clr	       LD      A,(faza)	       INC     A	       LD      (faza),A	       RETdermo	       XOR     A	       LD      (faza),A	       RETfaza	       DB      0clr	       LD      C,8	       PUSH    DEclr0	       PUSH    BC	       DUP   256	       LD      A,(DE)	       CPL	       LD      B,A	       LD      A,(HL)	       AND     B	       LD      B,A	       LD      A,(DE)	       LD      C,A	       LD      A,(IX)	       AND     C	       OR      B	       LD      (HL),A	       INC     L	       INC     IX	       EDUP	       POP     BC	       INC     DE	       INC     H	       DEC     C	       JP      NZ,clr0	       POP     DE	       RETdownde	       INC     D	       LD      A,D	       AND     7	       RET     NZ	       LD      A,E	       ADD     A,32	       LD      E,A	       RET     C	       LD      A,D	       SUB     8	       LD      D,A	       RETrnd	       LD      A,R	       AND     127	       LD      L,A	       LD      H,0	       ADD     HL,HL	       LD      DE,ytabl	       ADD     HL,DE	       LD      A,(HL)	       LD      E,A	       INC     HL	       LD      A,(HL)	       LD      D,A	       EX      DE,HL	       LD      A,R	       AND     15	       LD      D,0	       LD      E,A	       ADD     HL,DE	       RET;Печать	строки символов	2*2; ╤- адрес строки ( 0 -	конец строки); ╥- адрес на экранеprstr	       LD      (nachalo),HLprstr1	       LD      A,(DE)	       OR      A	       RET     Z	       CP      13	       CALL    Z,enter	       CP      " "	       JR      Z,probel	       PUSH    HL	       PUSH    DE	       CALL    prsym	       POP     DE	       POP     HLprobel	       INC     DE	       INC     L	       INC     L	       JR      prstr1nachalo	       DW      0enter	       LD      HL,(nachalo)	       LD      B,16sux	       CALL    downhl	       DJNZ    sux	       LD      (nachalo),HL	       INC     DE	       LD      A,(DE)	       RET;Печать	одного символа 2*2; ╥- адрес в экране;х▀- код символаprsym	       SUB     65	       PUSH    HL	       LD      L,A	       LD      H,0	       DUP   6	       ADD     HL,HL	       EDUP	       LD      DE,font	       ADD     HL,DE	       EX      DE,HL	       POP     HL	       LD      B,16hernia	       PUSH    HL	       DUP   2	       LD      A,(DE)	       AND     (HL)	       LD      (HL),A	       INC     DE	       LD      A,(DE)	       OR      (HL)	       LD      (HL),A	       INC     DE	       INC     L	       EDUP	       POP     HL	       CALL    downhl	       DJNZ    hernia	       RETdownhl	       INC     H	       LD      A,H	       AND     7	       RET     NZ	       LD      A,L	       ADD     A,32	       LD      L,A	       RET     C	       LD      A,H	       SUB     8	       LD      H,A	       RETtxt	       DB      "ACCEPT",0	       DB      "VIRTUAL",13	       DB      "BROTHERS",0	       DB      "DIGITAL",13	       DB      "REALITY",0	       DB      "ANTARES",0	       DB      "PROGRESS",0	       DB      "SYNDROME",0	       DB      "RAZZLERS",0	       DB      "E MAGE",0	       DB      "LIGHT",13	       DB      "FUTURE",0	       DB      "X TRADE",0	       DB      "CPU",0	       DB      "PROXIMA",13	       DB      "CENTAURI",0	       DB      "JEEZ",0	       DB      " HOT",13	       DB      "HEADS",0	       DB      "LEGACY",0	       DB      "BRAIN",13	       DB      "WAVE",0	       DB      "FATALITY",0	       DB      "PHANTASY",0	       DB      "FOURTH",13	       DB      "DIMENSIO",0	       DB      "PLAYGEAR",0	       DB      "FLASH",0	       DB      "TRIUMPH",0	       DB      "SERIOS",13	       DB      "MAKERS",0	       DB      "HARDWAVE",0	       DB      "  XL",13	       DB      "DESIGN",0	       DB      "ASPHYXIA",0	       DB      "DELIRIUM",13	       DB      "TREMENS",0	       DB      " DIE",13	       DB      "KRUPPS",0	       DB      "EXCESS",0	       DB      " POWER",13	       DB      "PROJECT",0	       DB      "BEERMANZ",0	       DB      "ETERNITY",13	       DB      "INDUSTRY",0	       DB      "SAGE",0	       DB      "EXTREME",0	       DB      "STYLE",0	       DB      "CONCERN",13	       DB      " CHAOS",0	       DB      1sklad	       DW      txtfont	       INCBIN   "LAMEFONT.C"chclr	       INCBIN   "CHUNX.C"ytabl	       DS      190	       DW      #4100	       DW      #4200	       DW      #4800	       DW      #4700	       DW      #4300	       DW      #4420	       DW      #4400	       DW      #4200	       DW      #4300	       DW      #4400	       DW      #4100	       DW      #4200	       DW      #4800	       DW      #4700	       DW      #4300	       DW      #4400	       DW      #4400	       DW      #4200	       DW      #4300	       DW      #4400	       DW      #4100	       DW      #4700	       DW      #4400	       DW      #4200	       DW      #4800	       DW      #4700	       DW      #4300	       DW      #4400	       DW      #4400	       DW      #4400	       DW      #4400	       DW      #4200	       DW      #4300	       DW      #4400endprog	       ORG     #c000	       INCBIN   "WOLF.C"	savesna "greets.sna",#8000